package com.abhinash.userrating.controller;import com.abhinash.userrating.domain.Rating;import com.abhinash.userrating.dto.UserRating;import com.abhinash.userrating.service.RatingService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/ratingsdata")public class RatingController {    @Autowired    RatingService ratingService;    @RequestMapping("/movies/{movieId}")    public Rating getMovieRating(@PathVariable("movieId") Long movieId) {        return ratingService.getMovieRating(movieId);    }    @RequestMapping(value = "/user/{userId}")    public UserRating getUserRating(@PathVariable String userId) {        return ratingService.getUserRating(userId);    }    @PostMapping("/rating")    public Rating addRating(@RequestBody Rating rating) {        return ratingService.saveRating(rating);    }}