package com.abhinash.userrating.service;import com.abhinash.userrating.domain.Rating;import com.abhinash.userrating.dto.UserRating;import com.abhinash.userrating.repos.RatingRepos;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class RatingService {    @Autowired    RatingRepos ratingRepos;    public Rating getMovieRating(Long movieId){        return ratingRepos.findByMovieId(movieId);    }    public UserRating getUserRating(String useId){        List<Rating> rating = ratingRepos.findByUserId(useId);        UserRating userRating = new UserRating();        userRating.setUserId(useId);        userRating.setRatings(rating);        return userRating;    }     public Rating saveRating(Rating rating){        return ratingRepos.save(rating);     }}